typedef unsigned int vsip_dda_sync_policy;

vsip_dda_sync_policy VSIP_DDA_SYNC_POLICY_IN = 0x01;
vsip_dda_sync_policy VSIP_DDA_SYNC_POLICY_OUT = 0x02;
vsip_dda_sync_policy VSIP_DDA_SYNC_POLICY_INOUT
  = VSIP_DDA_SYNC_POLICY_IN | VSIPL_DDA_SYNC_POLICY_OUT;
vsip_dda_sync_policy VSIP_DDA_SYNC_POLICY_COPY = 0x04;

